        //     self.proposalAnswer[_proposalId][i+1] = _hash[i];
        // }
        for(uint16 i=0;i<_hash.length;i++){
            self.proposalAnswer[_proposalId][i+1] = _hash[i];
        }
        self.responsedProposal[_proposalId] = true;
        return true;
    }


