    function createProposal(
        uint8 _status,
        address[] memory _voters,
        address _creator,
        uint256 _startTime,
        uint256 _expirationTime,
        string memory _disc,
        string[] calldata _option,
        uint256 _voteRight
    ) external {
        // start time and end time should be greater than current time ye hona chahiye but what does mean by "pool not started"?
        require(block.timestamp < _startTime, "proposal not started");
        require(
            block.timestamp < _expirationTime,
            "End time should be greater than current time"
        );
        require(
            _startTime < _expirationTime,
            "End time should be greater than start time"
        );
        proposalId++;
        proposals[proposalId].createProposal(
            _status,
            _voters,
            _creator,
            _startTime,
            _expirationTime,
            _disc,
            _voteRight,
            _option
        );
        emit ProposalCreated(
            proposalId,
            _voters,
            _creator,
            _startTime,
            _expirationTime,
            _disc,
            _option
        );
        validProposals[proposalId] = true;
        // 1 -> a=1 b=1 c=1 d=1
        // 10 -> a=10 b=10 c=10 d=10

        for (uint256 i = 0; i < _voters.length; i++) {
            mint(_voters[i], _voteRight);
            allVoters[_voters[i]][proposalId].createVoter(
                proposalId,
                _voters[i],
                false,
                _voteRight
            );
            emit mintAndburn("mint", _voters[i], _voteRight);
        }

        userProposal[_msgSender()].updateProposalList(proposalId);
    }

    function getProposalCount() public view returns (uint256) {
        return proposalId;
    }

    function responseProposal(
        uint256 _proposalId,
        address _voter,
        string[] memory _hash,
        uint256[] memory v
    ) external {
        // require(Iuser(admin.userContract()).isUserExist(_msgSender()),"User not exist");

        require(validProposals[_proposalId], "invalid proposalId");
        require(
            allVoters[_voter][_proposalId].proposalId == _proposalId,
            "not a valid voter for this proposal"
        );

        // ye chaya proposal voter[]!! // access
        require(!allVoters[_voter][_proposalId].hasVoted, "already voted");
        require(
            proposals[_proposalId].numOfOptions == _hash.length,
            "not a valid length of hash response!"
        );
        uint256 sum = 0;
        for (uint256 i = 0; i < v.length; i++) {
            sum += v[i] * v[i];
        }
        require(
            allVoters[_voter][_proposalId].voteNum >= sum,
            "not sufficient balance to cast vote"
        );

        proposals[_proposalId].responseProposal(_voter, _hash, v);
        userProposal[_msgSender()].updateResponseList(_proposalId, _hash);

        allVoters[_voter][_proposalId].updateVoteNum();
        allVoters[_voter][_proposalId].hasVoted = true;
        // burn(_voter,proposals[proposalId].voteRight);
