    {
        require(validProposals[_proposalId], "not a valid proposal");
        // require(proposals[proposalId].status ==3,"not completed yet");
        return (
            proposals[_proposalId].votes[proposals[_proposalId].result[1]],
            proposals[_proposalId].result[1]
        );
    }

