    function burnOwner(uint256 _proposalId, address[] memory _voters)
        external
        returns (bool)
    {
        require(
            block.timestamp > (proposals[_proposalId].expirationTime / 1000),
            "proposal is not completed yet"
        );

        for (uint256 i = 0; i < _voters.length; i++) {
            if (
