                allVoters[_voters[i]][_proposalId].proposalId == _proposalId &&
                allVoters[_voters[i]][_proposalId].burn == false
            ) {
                burn(_voters[i], proposals[_proposalId].voteRight);
                allVoters[_voters[i]][_proposalId].burn = true;
                emit mintAndburn(
